import React, {useState, Fragment} from 'react';

const InputUser = () => {

    const [inputs, setInputs] = useState(
        {
            name:""
        }
    );

    const { name } = inputs;

    const onChange = e =>
  setInputs({ ...inputs, [e.target.name]: e.target.value });


  const onSubmitForm = async e => {



    e.preventDefault();
    try {
      const body = { name  };
      const response = await fetch("http://localhost:5000/",{
        method: "POST",
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify(body)
      });

      console.log(response);
    } catch(err){
      console.error(err.message);
    }


  };

return(
    <Fragment>

    <form onSubmit={onSubmitForm}>
<p className="text-start fs-5">Name</p>
<input type="name" type="text" className="form-control my-3" id="floatingInput" placeholder="Name" onChange={e => onChange(e)}/>
<br></br>
<button className="w-100 btn btn-lg my-3 blue-bg text-white" style={{"height": "50px"}} type="submit">Create Account</button>

    </form>

    </Fragment>
)


};

export default InputUser
